content.query:
Resources
| where type =~ 'microsoft.compute/virtualmachines'
| project vmName = id, Status=properties.extended.instanceView.powerState.displayStatus, location, resourceGroup, vmSize=properties.hardwareProfile.vmSize, DiskSize=properties.storageProfile.osDisk.diskSizeGB, DiskSku=properties.storageProfile.osDisk.managedDisk.storageAccountType, DiskCaching=properties.storageProfile.osDisk.caching, OS=properties.extended.instanceView.osName, OSVersion=properties.extended.instanceView.osVersion, LicenseType=properties.licenseType, ["Azure Spot Eviction Policy"]=properties.evictionPolicy, subscriptionId
| sort by vmName


//| project name, properties.priority, properties.evictionPolicy

//Resources 
//| where type == "microsoft.compute/virtualmachines"
//| project id, location, resourceGroup, properties.hardwareProfile.vmSize

where type =~ "microsoft.storage/storageaccounts"
| extend AuthenticationSource = properties.azureFilesIdentityBasedAuthentication.directoryServiceOptions
| extend DomainName= properties.azureFilesIdentityBasedAuthentication.activeDirectoryProperties.domainName
//| extend Pack=pack_all()
//| project properties
| project Customer=subscriptionId, id, Path = properties['primaryEndpoints'][0]['file'], kind, location, resourceGroup, sku.tier, PrivateEndpoint=properties.privateEndpointConnections[0]['properties']['privateEndpoint']['id'], AuthenticationSource, DomainName

recoveryservicesresources
| where type == "microsoft.recoveryservices/vaults/backupfabrics/protectioncontainers/protecteditems"
| where name contains "VM"
//| mv-expand todynamic(properties.lastRecoveryPoint) to typeof(dynamic)
//| extend SplitAll=split(properties.lastRecoveryPoint, 'T')
//| extend ["Last Restore Point"] = SplitAll[0]
| extend Pack=pack_all()
| project Customer=subscriptionId, RecoveryServiceVault=id, VirtualMachine=properties.friendlyName, ProectionState=properties.protectionState, ProtectionStatus=properties.protectionStatus, CurrentProtectionState=properties.currentProtectionState, LastBackupStatus=properties.lastBackupStatus, ["Last Restore Point"]=properties.lastRecoveryPoint, ["Details"]=Pack

// Automatic update configuration 
// Automatic update configuration. 
// To create an alert for this query, click '+ New alert rule'
UpdateSummary
| summarize AggregatedValue = count() by WindowsUpdateSetting, Computer, _ResourceId
| order by WindowsUpdateSetting asc

// Automatic update configuration is disabled 
// Computers with automatic update disabled. 
// To create an alert for this query, click '+ New alert rule'
UpdateSummary
| where WindowsUpdateSetting == "Manual" 
| sort by TimeGenerated desc
