content.query:
// Success rate of establishing a connection state when attempted by the user
WVDConnections
| join kind=leftsemi
(
    WVDConnections
    | where TimeGenerated {TimeRange}
    | distinct CorrelationId
) on CorrelationId
//| where State =="Completed"// in ("Connected", "Completed")
| summarize arg_max(TimeGenerated, *) by CorrelationId
| where _ResourceId =~ '{HostPool}'
| join kind=leftouter
(
    WVDCheckpoints
    | where Source == "RDStack" and Name == "RdpStackConnectionEstablished"
    | project CorrelationId, Connected=true
) on CorrelationId
| extend Connected=coalesce(Connected, false)
// Split the activity ID into the "session ID" (first 36-4=32 characters of the GUID, same value for all reconnections) and the last 4 characters are the attempt index (0 for initial connection, increment for each reconnect)
| extend SessionId=substring(CorrelationId, 0, 36 - 4),
         AttemptIndex=toint(strcat("0x", substring(CorrelationId, 36 - 2), substring(CorrelationId, 36 - 4, 2))) // Swap the last two pairs of characters, and intrepret as hex
// For each attempt in a session, verify that checkpoints from the host confirm the connection
| summarize Success=countif(Connected)>0, StartTime=min(TimeGenerated) by SessionId
| make-series 
    SuccessSessions=dcountif(SessionId, Success) default=0,
    ErrorSessions=dcountif(SessionId, not(Success)) default=0
    on StartTime step {TimeRange:grain}
{FocusFailures}

// % of users able to reach a session host
WVDConnections
| join kind=leftsemi
(
    WVDConnections
    | where TimeGenerated {TimeRange}
    | distinct CorrelationId
) on CorrelationId
| summarize arg_max(TimeGenerated, *) by CorrelationId
| where _ResourceId =~ '{HostPool}'
| join kind=leftouter
(
    WVDCheckpoints
    | where Source == "RDStack" and Name == "RdpStackConnectionEstablished"
    | project CorrelationId, Connected=true
) on CorrelationId
| extend Connected=coalesce(Connected, false)
// For each user in each time bin, find out if they were able to connect
| summarize Connected=countif(Connected)>0 by UserName, TimeGenerated=bin_at(TimeGenerated, {TimeRange:grain}, {TimeRange:start})
| make-series SuccessUsers=dcountif(UserName, Connected), Total=dcount(UserName) on TimeGenerated step {TimeRange:grain}
| project TimeGenerated, SuccessUsers, ErrorUsers=series_subtract(Total, SuccessUsers)
{FocusFailures}

let SelectedActivities = dynamic([{ExpandActivities}]);
let GetPrettyError=(ErrorName:string, ErrorCode:string)
{
    iif(ErrorName !in ('', '<>'), strcat(ErrorName, ' (', ErrorCode, ')'), ErrorCode)
};
WVDErrors
| where SelectedActivities contains CorrelationId
| extend Error=GetPrettyError(CodeSymbolic, Code)
| summarize MessageSample=any(Message), Activities=dcount(CorrelationId) by ErrorKind=iif(ServiceError, "Service", "Deployment"), Source, Error

let GetPrettyError=(ErrorName:string, ErrorCode:string)
{
    iif(ErrorName !in ('', '<>'), strcat(ErrorName, ' (', ErrorCode, ')'), ErrorCode)
};
let IsSelectedCorrelation = (Selected:string, Current:string)
{
    let GuidStart = substring(Selected, 0, strlen(Selected)-1);
    (Selected contains "*" and Current startswith GuidStart)     or    Current == Selected
};
WVDCheckpoints
| where TimeGenerated {TimeRange} and IsSelectedCorrelation("{GroupedCorrelationId}", CorrelationId)
| project CorrelationId, TimeGenerated, Kind="Checkpoint", Source, Information=Name, Parameters
| as ResultSet
| union
(
    WVDErrors
    | where IsSelectedCorrelation("{GroupedCorrelationId}", CorrelationId)
    | project CorrelationId,TimeGenerated, Kind=iif(ServiceError, "Service", "Deployment"), Source, Information = GetPrettyError(CodeSymbolic, Code)
    | distinct *
)
| join kind = leftouter
(
    ResultSet
    | where Source == "RDStack" and Information == "RdpStackConnectionEstablished"
    | project CorrelationId, Connected=true
    | summarize Success=dcountif(CorrelationId, Connected)>0 by CorrelationId
) on CorrelationId
| extend CorrelationId=strcat(iif(Success, "", ""), " ", CorrelationId)
| project-away Success
| sort by CorrelationId asc, TimeGenerated asc

WVDErrors
//| where ServiceError != "false"
| project TimeGenerated, ServiceError, UserName, ActivityType, Source, Code, CodeSymbolic, Message, Operation, SourceSystem, Type, CorrelationId, _ResourceId
//| summarize count(UserName) by CodeSymbolic

