let GetHumanTimestamp = (Duration:timespan) {
    let Weeks   = Duration/7d;
    let Days    = Duration/1d;
    let Hours   = Duration/1h;
    let Minutes = Duration/1m;
    case(
        Weeks   > 1, strcat(      round(Weeks,   1),  " weeks"),
        Days    > 1, strcat(      round(Days,    1),  " days"),
        Hours   > 1, strcat(      round(Hours,   1),  " hours"),
        Minutes > 1, strcat(toint(round(Minutes, 0)), " minutes"),
                         "less than 1 minute"
        )                  
};
WVDConnections
| where UserName == "{UserName}" and _ResourceId =~ '{HostPool}'
| summarize arg_max(TimeGenerated, ClientType, ClientVersion, SessionHostName, ResourceAlias) by State
| extend HumanTimestamp = GetHumanTimestamp(now()-TimeGenerated), Host=trim_end(@"\..*", SessionHostName), ClientType = replace("com.microsoft.rdc.", "", ClientType)
| as SourceData
| where State == "Started"
| project State, TimeGenerated, Message=strcat("Is currently **attempting to connect** to **", Host, "** (for about **", HumanTimestamp, "**), using **", ClientType, "** version **", ClientVersion, "** and the **", ResourceAlias, "** resource.")
| union
(
    SourceData
    | where State == "Completed"
    | project State, TimeGenerated, Message=strcat("Last seen after **disconnecting** from **", Host, "** about **", HumanTimestamp, "** ago, having used **", ClientType, "** version **", ClientVersion, "** to launch **", ResourceAlias, "**")
)
| union
(
    SourceData
    | where State == "Connected"
    | project State, TimeGenerated, Message=strcat("**Currently connected** to **", Host, "** for about **", HumanTimestamp, "** using **", ClientType, "** version **", ClientVersion, "** via the **", ResourceAlias, "** resource.")
)
| summarize arg_max(TimeGenerated, State, Message) by Selected=true
| project Value=State, Label=Message, Selected
